import React from "react";

class Texto extends React.Component {
  render() {
    const {callback, name, titulo } = this.props
    return (
      <label>
      {titulo}:
      <textarea 
      name={name} 
      // value={comentario}
      onChange={callback}
      ></textarea>
    </label>
    )
  }
}

export default Texto
