import React from 'react'
import './App.css';

class Form extends React.Component {
  constructor() {
    super()
    this.handleChange = this.handleChange.bind(this)
    this.state = {
      name: '',
      email: '',
      comentario: '',
      preferencia: '',
      fixa: false,
    }
  }

  handleChange(event) {
    this.setState({
      name: event.target.value
    })
    // console.log(event.target.value)
  }

  render() {
    const {name, email, comentario, preferencia} = this.state
    return (
      <form className="App-header" >
        <label>
          Nome:
          <input 
          name='name'
          type="text" 
          value={name} 
          onChange={this.handleChange}></input>
        </label>
        <label>
          Email:
          <input 
          name='email' 
          type="text" 
          value={email}
          onChange={this.handleChange}></input>
        </label>
        <label>
          Comentário:
          <textarea 
          name='comentario' 
          value={comentario}
          onChange={this.handleChange}></textarea>
        </label>
        <label >
          Preferência
          <select  name='preferência' value={preferencia}>
            <option value = 'Azul'> Azul </option>
            <option value = 'Verde'> Verde</option>
            <option value = 'Amarelo'> Amarelo</option>
            <option value='Batata'>Batata </option>
            <option value = 'Goiania'> Goiania</option>
          </select>
        </label>
        <label>
          Fixa?:
          <input name='fixa' type="checkbox"></input>
        </label>
      </form>
    )
  }
}

export default Form;
