import React from 'react'
import './App.css';

class Form extends React.Component {
  constructor() {
    super()
    this.handleChange = this.handleChange.bind(this)
    this.state = {
      name: '',
      email: '',
      comentario: '',
      preferencia: '',
      fixa: 'on',
    }
  }

  handleChange({target}) {
    const { value, name } = target
    this.setState({
      [name]: value,
      // fixa: target.checked,
    })
    // console.log(event.target.value)
  }

  render() {
    const {name, email, comentario, preferencia, fixa} = this.state
    return (
      <form className="App-header" >
        <label>
          Nome:
          <input 
          name='name'
          type="text" 
          // value={name} 
          onChange={this.handleChange}
          ></input>
        </label>

        <label>
          Email:
          <input 
          name='email' 
          type="text" 
          value={email}
          onChange={this.handleChange}
          ></input>
        </label>

        <label>
          Comentário:
          <textarea 
          name='comentario' 
          value={comentario}
          onChange={this.handleChange}
          ></textarea>
        </label>

        <label >
          Preferência
          <select  
          name='preferência' 
          value={preferencia}
          onChange={this.handleChange}
          >
            <option value = 'Azul'> Azul </option>
            <option value = 'Verde'> Verde</option>
            <option value = 'Amarelo'> Amarelo</option>
            <option value='Batata'>Batata </option>
            <option value = 'Goiania'> Goiania</option>
          </select>
        </label>

        <label>
          Fixa?:
          <input 
          name='fixa' 
          type="checkbox"
          value='checked'
          onChange={this.handleChange}
          ></input>
        </label>
      </form>
    )
  }
}

export default Form;
