import React from "react";

class Texto extends React.Component {
  render() {
    let msg = undefined;
    const pequeno = 'Texto muito pequeno'
    const medio = 'Ok'
    const grande = 'Texto muito grande'
    const pare = 'Pare imediatamente'
    const {callback, name, titulo, erro } = this.props
    if (erro.length > 10) msg = pequeno;
    if (erro.length > 20) msg = medio;
    if (erro.length > 30) msg = grande;
    if (erro.length > 50) msg = pare;
    return (
      <div>
        <label>
          {titulo}:
          <textarea 
          name={name} 
          // value={comentario}
          onChange={callback}
          ></textarea>
        </label>
        <span id='pequeno'>{msg}</span>
      </div>

    )
  }
}

export default Texto
