import React from "react";

class Texto extends React.Component {
  render() {
    // let msg = undefined;
    let pequeno = undefined
    let medio = undefined 
    let grande = undefined 
    let pare = undefined
    const {callback, name, titulo, erro } = this.props
    if (erro.length > 5 && erro.length <= 20 ) pequeno = 'Texto muito pequeno';
    if (erro.length > 20 && erro.length <= 30) medio = 'Ok';
    if (erro.length > 30 && erro.length <= 50) grande = 'Texto muito grande';
    if (erro.length > 50) pare = 'Pare imediatamente';
    return (
      <div>
        <label>
          {titulo}:
          <textarea 
          name={name} 
          // value={comentario}
          onChange={callback}
          ></textarea>
        </label>
        <span id='pequeno'>{pequeno}</span>
        <span id='medio'>{medio}</span>
        <span id='grande'>{grande}</span>
        <span id='pare'>{pare}</span>
      </div>

    )
  }
}

export default Texto
